%script

-- Cleanup old settings table
BEGIN EXECUTE IMMEDIATE 'DROP TABLE CORONA_CONFIRMED_settings';
EXCEPTION WHEN OTHERS THEN NULL; END;
/

-- Cleanup old model with the same name
BEGIN DBMS_DATA_MINING.DROP_MODEL('CORONA_CONFIRMED_SAMPLE');
EXCEPTION WHEN OTHERS THEN NULL; END;
/


CREATE TABLE CORONA_CONFIRMED_SETTINGS(SETTING_NAME VARCHAR2(30), 
                             SETTING_VALUE VARCHAR2(128));
BEGIN
  -- Select ESM as the algorithm
  INSERT INTO CORONA_CONFIRMED_SETTINGS 
         VALUES(DBMS_DATA_MINING.ALGO_NAME,
                DBMS_DATA_MINING.ALGO_EXPONENTIAL_SMOOTHING);
  -- Set accumulation interval to be a day
   INSERT INTO CORONA_CONFIRMED_SETTINGS 
         VALUES(DBMS_DATA_MINING.EXSM_INTERVAL,
                DBMS_DATA_MINING.EXSM_INTERVAL_DAY);
  -- Set prediction step to be 4 days 
  INSERT INTO CORONA_CONFIRMED_SETTINGS 
         VALUES(DBMS_DATA_MINING.EXSM_PREDICTION_STEP,'4');
  -- Set ESM model to be Simple
  INSERT INTO CORONA_CONFIRMED_SETTINGS 
         VALUES(DBMS_DATA_MINING.EXSM_MODEL,
                DBMS_DATA_MINING.EXSM_SIMPLE);
  -- Set seasonal cycle to be 4 days
  --INSERT INTO ESM_SH_SETTINGS 
  --    VALUES(DBMS_DATA_MINING.EXSM_SEASONALITY,'365');
END;
/

-- Build the Exponential Smotthing (ESM) model
BEGIN
  DBMS_DATA_MINING.CREATE_MODEL(MODEL_NAME => 'CORONA_CONFIRMED_SAMPLE',
               MINING_FUNCTION     => 'TIME_SERIES',
               DATA_TABLE_NAME     => 'CORONA_CONFIRMED',
               CASE_ID_COLUMN_NAME => 'EVENTDATE',
               TARGET_COLUMN_NAME  => 'CONFIRMED',
               SETTINGS_TABLE_NAME => 'CORONA_CONFIRMED_SETTINGS');
END;
/

